$output = print_folder_helper_pages_1($folder, $depth);\r\n		$output .= print_folder_helper_folders_1($folder, $depth);\r\n		\r\n	if (strlen($output) > 0)\r\n	{\r\n		$tpl = str_replace(''{id}'', $folder[''idcat''], "{layer}\r\n"); \r\n		$output = str_replace(''{layer}'', $output, $tpl);\r\n	}\r\n	\r\n	return $output;\r\n}\r\n\r\n\r\nfunction print_folder_helper_pages_1($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_side, $mod, $sess;\r\n	\r\n	$cats_no_pages = explode(",", trim(str_replace('' '', '''', "")));\r\n	\r\n	// Seiten ausgeben\r\n	if (isset($folder[''pages'']))\r\n	{  \r\n	  $pagevals = array_values($folder[''pages'']);\r\n	  if(! in_array($con_side[ $pagevals[''0''] ][''idcat''],$cats_no_pages) || ('''' != ''true'' && $sess->name == ''sefrengo'' ) )\r\n    {\r\n	   \r\n  		foreach ($folder[''pages''] as $idcatside)\r\n  		{\r\n  			$template = ($idcatside == 3) ? "<a class=\\"navi\\" href=\\"{link}\\">{name}</a>\r\n" : "<a class=\\"navi\\" href=\\"{link}\\">{name}</a>";\r\n  			$item = str_replace(''{name}'', htmlspecialchars($con_side[$idcatside][''name''], ENT_COMPAT, ''UTF-8''), $template);\r\n  			$item = str_replace(''{desc}'', htmlspecialchars(get_side_desc($idcatside,''desc''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{summary}'', htmlspecialchars(get_side_desc($idcatside,''summary''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{link}'', $con_side[$idcatside][''link''], $item);\r\n  			$item = str_replace(''{id}'', $idcatside, $item);\r\n  			$item = str_replace(''{depth}'', $depth, $item);\r\n  			if (strstr($item, ''{tabindex}'') !== false)\r\n  			{\r\n  				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n  			}\r\n  \r\n  			  \r\n  			$temp .= $item;\r\n  		}\r\n  	}\r\n	}\r\n	\r\n	return $temp;\r\n}\r\n\r\n\r\nfunction print_folder_helper_folders_1($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_tree, $con_side, $mod;\r\n  \r\n  $cats_no_pages = explode(",", trim(str_replace('' '', '''', "")));\r\n  \r\n	// Ordner ausgeben\r\n	if (is_array($folder[''children'']))\r\n	{\r\n		// Alle Unterordner dieses Ordners durchgehen\r\n		foreach ($folder[''children''] as $subfolder)\r\n		{\r\n			$idcat = $subfolder[''idcat''];\r\n			$active_side_is_start = 0;\r\n      $active_cat = 1;\r\n      $show_start_page = "true";\r\n				\r\n							// ... andernfalls die Vorlage "Aktiver Ordner" benutzen, wenn der Unterordner\r\n				// der aktuelle ("angezeigte") Ordner ist und sonst die Vorlage "Ordner" verwenden.\r\n				$template = ($idcat == 1) ? "<a class=\\"navi\\" href=\\"{link}\\">{name}</a>{folder}\r\n" : "<a class=\\"navi\\" href=\\"{link}\\">{name}</a>{folder}\r\n";\r\n				\r\n			$item = str_replace(''{name}'', htmlspecialchars($con_tree[$idcat][''name''], ENT_COMPAT, ''UTF-8''), $template);\r\n			$item = str_replace(''{desc}'', htmlspecialchars(get_cat_desc($idcat), ENT_COMPAT, ''UTF-8''), $item);\r\n			//$item = str_replace(''{link}'', $con_tree[$idcat][''link''], $item);\r\n			if ('''' == ''true'')\r\n			{\r\n                $item = str_replace(''{link}'', $con_side[ $GLOBALS[''SF_catinfos'']->getIdcatsideStartpage($idcat) ][''link''], $item);\r\n			} else {\r\n			     $item = str_replace(''{link}'', $con_tree[$idcat][''link''], $item);\r\n			} \r\n			\r\n			$item = str_replace(''{id}'', $idcat, $item);\r\n			$item = str_replace(''{depth}'', $depth, $item);\r\n			if (strstr($item, ''{tabindex}'') !== false)\r\n			{\r\n				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n			}\r\n\r\n			\r\n			$subfolder_data = print_folder_1($subfolder, $depth+1);\r\n						\r\n			$temp .= str_replace("{folder}", $subfolder_data, $item);\r\n		}\r\n	}\r\n\r\n	return $temp;	\r\n}\r\n\r\n\r\nfunction build_navi_tree_1($start, $depth, $first = false)\r\n{\r\n	global $catlist, $con_tree, $con_side, $cms_side, $db, $mod, $cms_db, $lang;\r\n\r\n	$entry[''idcat''] = $start;\r\n	\r\n	if ($first)\r\n	{\r\n		$entry[''first''] = true;\r\n	}\r\n\r\n	else if (!in_array($start, $mod[''path''])) return $entry;	\r\n\r\n	// $hide == true <=> Ausgeschlossene Seiten und Ordner verbergen\r\n	$hide = (!isset($cms_side[''view'']));\r\n\r\n			// Alle zu diesem Ordner gehoerigen Seiten zusammensuchen\r\n		foreach ($con_side as $idcatside=>$page)\r\n		{\r\n			// Seite ins Array aufnehmen, wenn ...\r\n			if ( ($page[''idcat''] == $start) &&\r\n                 (\r\n				   				   (\r\n				     // ... wir im Backend sind, oder ...\r\n				     isset($cms_side[''view'']) ||\r\n				     // ... diese online ist ...\r\n				     ($con_side[$idcatside][''online''] != 0)\r\n				   )\r\n				 ) &&\r\n				 // ... und diese Seite nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Seiten dort angezeigt werden sollen)\r\n				 (!$hide || !strstr('',,'', ",$idcatside,"))\r\n			   )\r\n			{\r\n				$entry[''pages''][] = $idcatside;\r\n				\r\n							}\r\n		}\r\n		//print_r($entry[''pages'']);print_r($cms_mod);echo "xxxxxxxxxxxxxxxx";\r\n		// seiten nur wenn mindestens zwei \r\n		if (''true'' == ''gt1'') {\r\n		  if (count($entry[''pages'']) < 2) {\r\n		      unset($entry[''pages'']);\r\n		  }\r\n		}\r\n		// Seiten sortieren\r\n		if (is_array($entry[''pages'']))\r\n		{\r\n			foreach ($entry[''pages''] as $idcatside)\r\n			{\r\n				$sortarray[] = $con_side[$idcatside][''sortindex''];\r\n			}\r\n			array_multisort($sortarray, SORT_NUMERIC, SORT_ASC, $entry[''pages'']);\r\n		}\r\n\r\n	\r\n\r\n			\r\n		// Lokale Kopie von $catlist, weil der "foreach zeiger" fuer\r\n		// das globale Array durch die Rekursion verloren gehen wuerde\r\n		$catlist2 = $catlist;\r\n\r\n		// Fuer jeden Unterordner dieses Ordners diese Funktion aufrufen (Seiten und Unterordner zusammensuchen)\r\n		foreach ($catlist2 as $idcat)\r\n		{\r\n			// Unterordner ins Array aufnehmen, wenn ...\r\n			if ( ($con_tree[$idcat][''parent''] == $start) &&\r\n			     (\r\n			       // ... wir im Backend sind, oder ...\r\n			       isset($cms_side[''view'']) ||\r\n			       // ... dieser sichtbar/online ist ...\r\n			       ($con_tree[$idcat][''visible''] != 0)\r\n			     ) &&\r\n			     // ... und dieser Ordner nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Ordner dort angezeigt werden sollen)\r\n			     (!$hide || !strstr('',,'', ",$idcat,"))\r\n			   )\r\n			{\r\n				$entry[''children''][$idcat] = build_navi_tree_1($idcat, $depth+1);\r\n			}\r\n		}\r\n\r\n\r\n		// Ordner sortieren\r\n		if (is_array($entry[''children'']))\r\n		{\r\n			unset($sortarray);\r\n							$catlist2 = array();\r\n				foreach ($catlist as $key=>$value)\r\n				{\r\n					if (is_array($entry[''children''][$value]))\r\n					{\r\n						$catlist2[$value] = $key;\r\n					}\r\n				}\r\n	\r\n				foreach ($entry[''children''] as $idcat => $v)\r\n				{\r\n					$sortarray[] = $catlist2[$idcat];\r\n				}\r\n				\r\n				array_multisort($sortarray, SORT_NUMERIC, SORT_ASC, $entry[''children'']);\r\n					}\r\n\r\n	\r\n	if ($first)\r\n	{\r\n			}\r\n\r\n	return $entry;\r\n}\r\n\r\n\r\n/*\r\n * "Hauptprogramm"\r\n */\r\n\r\n	// Pfad vom aktuellen Ordner zum Start-Ordner "berechnen"\r\n	$mod[''current_idcat''] = 1;\r\n	$mod[''path''] = array();\r\n		\r\n	while ($mod[''current_idcat''] != 1)\r\n	{\r\n		$mod[''path''][] = $mod[''current_idcat''];\r\n		$mod[''current_idcat''] = $con_tree[$mod[''current_idcat'']][''parent''];\r\n\r\n		\r\n			if ($mod[''current_idcat''] == 0)\r\n			{\r\n				// ... aber wir dort angekommen sind und den Startordner noch nicht gefunden haben,\r\n				// liegt die aktuelle Seite nicht im "Pfad"\r\n								$mod[''path''] = array();\r\n				break;\r\n			}\r\n\r\n			}\r\n	\r\n	\r\n// Mit tabindex + 1 anfangen, wenn der Startordner ungleich Root ist, da in "Einleitung der Navigation" der tabindex benutzt werden koennte\r\n$evil_global_variable_for_easy_tabindex_implementation = 2;\r\n\r\n// Die Liste gemaess den gewaehlten Einstellungen aufbauen\r\n$mod[''output''] = print_folder_1(build_navi_tree_1(1, 0, true), 1);\r\n\r\nif ($mod[''output''] != "") {\r\n$mod[''intro''] = "";\r\n$mod[''outro''] = "";\r\n\r\n// Templatevariablen in Einleitung/Ende der Navigation ersetzen, falls der Startordner ungleich "root" ist und der Ordner sichtbar ist.\r\nif (true &&\r\n    ((isset($cms_side[''view'']) && true) ||\r\n    ($con_tree[1][''visible''] != 0)) )\r\n{\r\n	$mod[''intro''] = str_replace(''{name}'', htmlspecialchars($con_tree[1][''name''], ENT_COMPAT, ''UTF-8''), $mod[''intro'']);\r\n	$mod[''intro''] = str_replace(''{desc}'', htmlspecialchars(get_cat_desc(1), ENT_COMPAT, ''UTF-8''), $mod[''intro'']);\r\n	$mod[''intro''] = str_replace(''{link}'', $con_tree[1][''link''], $mod[''intro'']);\r\n	$mod[''intro''] = str_replace(''{id}'', ''1'', $mod[''intro'']);\r\n	$mod[''intro''] = str_replace(''{tabindex}'', ''1'', $mod[''intro'']);\r\n	\r\n	$mod[''outro''] = str_replace(''{name}'', htmlspecialchars($con_tree[1][''name''], ENT_COMPAT, ''UTF-8''), $mod[''outro'']);\r\n	$mod[''outro''] = str_replace(''{desc}'', htmlspecialchars(get_cat_desc(1), ENT_COMPAT, ''UTF-8''), $mod[''outro'']);\r\n	$mod[''outro''] = str_replace(''{link}'', $con_tree[1][''link''], $mod[''outro'']);\r\n	$mod[''outro''] = str_replace(''{id}'', ''1'', $mod[''outro'']);\r\n	$mod[''outro''] = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation, $mod[''outro'']);\r\n}\r\n\r\n// Navigation ausgeben\r\necho $mod[''intro''] . $mod[''output''] . $mod[''outro''];\r\n\r\n}\r\n\r\n\r\n\r\n\r\nunset ($mod, $evil_global_variable_for_easy_tabindex_implementation);\r\n\r\n ?>\r\n<!--END 1//-->\r\n    <div style="float: right; margin-right: 20px; font-size: 9px;">\r\n    LBANet  v0.755\r\n    </div>\r\n   </div>\r\n </div>\r\n<!--START 2//--><?PHP  $cms_mod[''container''][''id'']=''2'';  ?><?PHP  $cms_mod[''modul''][''id'']=''1'';$cms_mod[''key''] = ''mod''.$cms_mod[''contain