ontainer\r\n$frm->set_email_receiptor_get_mode_data($_REQUEST[''contactidcs''], $_REQUEST[''contactcontainer'']); \r\n//settings for email receiptor mode ''textfield''\r\n$frm->set_email_receiptor_textfield_mode_data($mvars[''12'']);\r\n//emailadress sender\r\n$frm->set_email_sender((is_numeric($mvars[''27'']))? \r\n							$_REQUEST[''go_frm''.$mvars[''27''] ]: $mvars[''12'']);\r\n//email subject\r\n$frm->set_email_subject($mvars[''11'']);\r\n//success message\r\n$frm->set_form_message_success($mvars[''14'']);\r\n//error message\r\n$frm->set_form_message_error($mvars[''13'']);\r\n//submitbutton text\r\n$frm->set_submitbutton_text($mvars[''24'']);\r\n//mutiple value separator\r\n$frm->set_mutiple_value_separator($mvars[''36'']);\r\n//key/ value value separator\r\n$frm->set_key_value_separator($mvars[''37'']);\r\n//upload dir\r\n$frm->set_upload_dir($cfg_cms[''cms_path'']. ''upload/in/'');\r\n//fallback {textfromdynamic}\r\n$frm->set_fallback_textfromdynamic($mvars[''54'']);\r\n//fallback {textareafromdynamic}\r\n$frm->set_fallback_textareafromdynamic($mvars[''55'']);\r\n//charset\r\n$frm->set_charset($lang_charset);\r\n//tple email\r\n$frm->set_tpl_email($mvars[''56'']);\r\n//send email\r\n$frm->set_send_mail($mvars[''65''] == ''true'');\r\n//en/ disable logging\r\n$frm->set_logging_active($mvars[''63''] == ''true'');\r\n//logfile\r\n$frm->set_logging_file($mvars[''64'']);\r\n//cc and bcc\r\n$frm->set_email_receiptor_cc($mvars[''57'']);\r\n$frm->set_email_receiptor_bcc($mvars[''58'']);\r\n//confirmmail vals: boolean sendiit|subject|body\r\n$frm->set_confirmmail_vars( ($mvars[''68''] == ''true''), $mvars[''69''], $mvars[''70'']);\r\n//addon object string\r\n$frm->set_addon($mvars[''72'']);\r\n//captcha vals\r\n$frm->set_captcha_vals($mvars[''73''], $mvars[''74''], $mvars[''75''], $mvars[''76''], $mvars[''77''], $mvars[''78'']);\r\n//second email\r\n$frm->set_second_email_sender($mvars[''80'']);\r\n$frm->set_second_email_recipient($mvars[''81'']);\r\n//requirenotice\r\n$frm->set_tpl_requirenotice($mvars[''82'']);\r\n//labeltext\r\n$frm->set_tpl_labeltext($mvars[''83'']);\r\n\r\n$frm_control =& new gooseberry_formcontroller;\r\n$frm_control->set_formobject($frm);\r\n$frm_control->set_db_mode_active($mvars[''59''] == ''true'' && $mvars[''reportviewer_included'']);\r\n$frm_control->set_db_mode_default_datagroup($mvars[''60'']);\r\n$frm_control->set_db_mode_items_are_online( ($mvars[''71''] == ''online'') );\r\n$frm_control->set_mutiple_value_separator($mvars[''36'']);\r\n$frm_control->set_save_in_filemanager($mvars[''61'']);\r\n$frm_control->set_filemanager_iddirectory($mvars[''62'']);\r\n$frm_control->set_redirect_after_success( (trim($mvars[''67'']) != ''''), $mvars[''67'']);\r\n$frm_control->start();\r\n//print_r($mvars);\r\necho $frm_control->get_content();\r\n\r\nunset($frm, $frm_control, $inner_form, $outer_form, $to_replace, $replacer, $frm_vals, $mvars, $a);\r\n\r\n\r\n\r\n}\r\n\r\nunset($is_dev);\r\n\r\n</CMSPHP>', '', 0, 1, 1, 1159227335, 1215636767, 'mod:c7ec67c74104a45bde479c5d666bdc75:256d7c93', '', '', '', 0, '0', '', '1');
