''] = $idcatside;\r\n$ln_routing[''idcat''] = $idcat;\r\n$ln_routing[''routings''] = array();\r\n$ln_routing[''idcat_temp''] = $ln_routing[''idcatside_temp''] = false;\r\n$ln_routing[''raw''] = trim( str_replace('' '', '''', "MOD_VALUE[30]"));\r\n\r\nif (strlen($ln_routing[''raw'']) > 14) {\r\n  $ln_routing[''raw_vals''] = explode("\\n", $ln_routing[''raw'']);\r\n  foreach ($ln_routing[''raw_vals''] AS $v) {\r\n    $pieces = explode(''>'', $v);\r\n    $ln_routing[''routings''][ $pieces[''0''] ] = $pieces[''1''];\r\n  }\r\n}\r\n\r\n\r\n//source idcatside\r\nif (array_key_exists(''idcatside:''.$idcatside, $ln_routing[''routings''])) {\r\n  //idcatside to idcatside\r\n  if( strstr ( $ln_routing[''routings''][''idcatside:''.$idcatside], ''idcatside'')) {\r\n    $ln_routing[''idcatside_temp''] = (int) str_replace(''idcatside:'', '''', $ln_routing[''routings''][''idcatside:''.$idcatside]);\r\n	if ($ln_routing[''idcatside_temp''] > 0) {\r\n	  $idcatside = $ln_routing[''idcatside_temp''];\r\n	  $idcat = $con_side[$idcatside][''idcat''];\r\n	}\r\n  //idcatside to idcat\r\n  } else {\r\n  	$ln_routing[''idcat_temp''] = (int) str_replace(''idcat:'', '''', $ln_routing[''routings''][''idcatside:''.$idcatside]);\r\n    if ($ln_routing[''idcat_temp''] > 0) {\r\n		$idcat = $ln_routing[''idcat_temp''];\r\n		$idcatside = $SF_catinfos->getIdcatsideStartpage($idcat);\r\n    }\r\n  }\r\n// source idcat\r\n} else if (array_key_exists(''idcat:''.$idcat, $ln_routing[''routings''])) {\r\n	//idcat to idcatside\r\n	if( strstr ( $ln_routing[''routings''][''idcat:''.$idcat], ''idcatside'')) {\r\n	    $ln_routing[''idcatside_temp''] = (int) str_replace(''idcatside:'', '''', $ln_routing[''routings''][''idcat:''.$idcat]);\r\n	    if ($ln_routing[''idcatside_temp''] > 0) {\r\n			$idcatside = $ln_routing[''idcatside_temp''];\r\n			$idcat = $con_side[$idcatside][''idcat''];\r\n	    }\r\n	//idcat to idcat\r\n	} else {\r\n		$ln_routing[''idcat_temp''] = (int) str_replace(''idcat:'', '''', $ln_routing[''routings''][''idcat:''.$idcat]);\r\n		if ($ln_routing[''idcat_temp''] > 0) {\r\n		  $idcat = $ln_routing[''idcat_temp''];\r\n		  $idcatside = $SF_catinfos->getIdcatsideStartpage($idcat);\r\n		}\r\n	}\r\n}\r\n\r\n\r\n</CMSPHP:CACHE>\r\n\r\n\r\n\r\n//Description of cats\r\nif(! function_exists(get_cat_desc)){\r\n    function get_cat_desc($a)\r\n{\r\nglobal $db,$cms_db;\r\n$sql  = "SELECT\r\n			*\r\n		FROM \r\n	   ".$cms_db[''cat_lang'']." \r\n		WHERE idcatlang = ''".$a."''";\r\n$db->query($sql);\r\n$db->next_record();\r\nreturn $db->f(''description''); \r\n}}\r\n\r\n//Descritpion of pages\r\nif(! function_exists(get_side_desc)){\r\n    function get_side_desc($a,$which)\r\n{\r\nglobal $db,$cms_db;\r\n$sql  = "SELECT\r\n			*\r\n		FROM \r\n	   ".$cms_db[''side_lang'']." LEFT JOIN\r\n		".$cms_db[''cat_side'']." USING(IDSIDE)\r\n		WHERE \r\nidcatside = ''".$a."''";\r\n$db->query($sql);\r\n$db->next_record();\r\n\r\nif ($which == ''desc'') {\r\nreturn $db->f(''meta_description''); }\r\nelseif ($which == ''summary''){\r\nreturn $db->f(''summary''); }\r\n}}\r\n\r\n\r\n\r\n\r\n\r\n\r\n//global $evil_global_variable_for_easy_tabindex_implementation, $mod;\r\n$evil_global_variable_for_easy_tabindex_implementation = 1;\r\n\r\n$mod[''desc''] = array();\r\n\r\n// idcat des Start-Ordners besorgen\r\n<CMSPHP:CACHE>\r\nif(! function_exists(''nav_negotiate_startlevel'') ){\r\nfunction nav_negotiate_startlevel( $current_cat, $startlevel ){\r\n        global $con_tree;\r\n\r\n        //echo "$cat, $searcharray <br />";//return false;\r\n        if (empty($current_cat) || $current_cat == 0 || ! is_int($current_cat) || ! is_int($startlevel)  ) {\r\n                return $current_cat;\r\n        }\r\n\r\n        $levels = array();\r\n        array_push($levels, $current_cat);\r\n        $cat = $current_cat;\r\n        while(true) {\r\n                $cat = (int) $con_tree[$cat][''parent''];\r\n                array_push($levels, $cat);\r\n                if ($cat == 0) break;\r\n        }\r\n        $levels_rev = array_reverse($levels);\r\n        //print_r($levels_rev);exit;\r\n\r\n        if ($levels_rev[$startlevel] > 0) {\r\n                return $levels_rev[$startlevel];\r\n        } else {\r\n                return $current_cat;\r\n        }\r\n}}\r\n\r\n\r\nif ($cms_mod[''value''][1] == ''root'')\r\n{\r\n        $cms_mod[''value''][1] = ''0'';\r\n}\r\nelseif ($cms_mod[''value''][1] == ''this'')\r\n{\r\n        $cms_mod[''value''][1] = $idcat;\r\n}\r\n\r\n$mod[''startlevel'']  = (int) "MOD_VALUE[29]";\r\nif ($mod[''startlevel''] > 0) {\r\n  $cms_mod[''value''][1] = (string) nav_negotiate_startlevel( (int) $idcat, $mod[''startlevel'']);\r\n}\r\n\r\nif (!$cms_mod[''value''][14])\r\n{\r\n	// Update von einer Version kleiner als 0.9-dev => Diese Variablen wurden noch nicht durch die Modulkonfiguration gesetzt\r\n	$cms_mod[''value''][14] = ''sortindex'';\r\n	$cms_mod[''value''][15] = ''SORT_ASC'';\r\n}\r\n\r\n\r\n// Anzahl darzustellender Ebenen auf 0 setzen, falls unsinnige Werte angegeben wurden\r\nif (!is_numeric($cms_mod[''value''][5]) || ($cms_mod[''value''][5] < 0))\r\n{\r\n	$cms_mod[''value''][5] = 0;\r\n}\r\n\r\n// Startnummer fuer tabindex auf 1 setzen, falls unsinnige Werte angegeben wurden\r\nif (!is_numeric($cms_mod[''value''][13]) || ($cms_mod[''value''][13] < 1))\r\n{\r\n	$cms_mod[''value''][13] = 1;\r\n}\r\n</CMSPHP:CACHE>\r\n\r\n\r\n/*\r\n * Hilfs-Funktionen\r\n */\r\n\r\nfunction print_folder_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][16] == ''true'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		$output = print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n		$output .= print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n	<CMSPHP:CACHE>\r\n	}\r\n	else\r\n	{\r\n	</CMSPHP:CACHE>\r\n		$output = print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n		$output .= print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n	<CMSPHP:CACHE>\r\n	}\r\n	</CMSPHP:CACHE>\r\n	\r\n	if (strlen($output) > 0)\r\n	{\r\n		$tpl = str_replace(''{id}'', $folder[''idcat''], "MOD_VALUE[26]"); \r\n		$output = str_replace(''{layer}'', $output, $tpl);\r\n	}\r\n	\r\n	return $output;\r\n}\r\n\r\n\r\nfunction print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_side, $mod, $sess;\r\n	\r\n	$cats_no_pages = explode(",", trim(str_replace('' '', '''', "MOD_VALUE[28]")));\r\n	\r\n	// Seiten ausgeben\r\n	if (isset($folder[''pages'']))\r\n	{  \r\n	  $pagevals = array_values($folder[''pages'']);\r\n	  if(! in_array($con_side[ $pagevals[''0''] ][''idcat''],$cats_no_pages) || (''MOD_VALUE[6]'' != ''true'' && $sess->name == ''sefrengo'' ) )\r\n    {\r\n	   \r\n  		foreach ($folder[''pages''] as $idcatside)\r\n  		{\r\n  			$template = ($idcatside == <CMSPHP:CACHE>echo $idcatside;</CMSPHP:CACHE>) ? "MOD_VALUE[23]" : "MOD_VALUE[22]";\r\n  			$item = str_replace(''{name}'', htmlspecialchars($con_side[$idcatside][''name''], ENT_COMPAT, ''UTF-8''), $template);\r\n  			$item = str_replace(''{desc}'', htmlspecialchars(get_side_desc($idcatside,''desc''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{summary}'', htmlspecialchars(get_side_desc($idcatside,''summary''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{link}'', $con_side[$idcatside][''link''], $item);\r\n  			$item = str_replace(''{id}'', $idcatside, $item);\r\n  			$item = str_replace(''{depth}'', $depth, $item);\r\n  			if (strstr($item, ''{tabindex}'') !== false)\r\n  			{\r\n  				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n  			}\r\n  \r\n  			<CMSPHP:CACHE>\r\n  			if (strstr($cms_mod[''value''][22], ''{first}'') || strstr($cms_mod[''value''][23], ''{first}''))\r\n  			{\r\n  			</CMSPHP:CACHE>\r\n  				if ($folder[''first''])\r\n  				{\r\n  					$folder[''first''] = false;\r\n  					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\1", $item);\r\n  				}\r\n  				else\r\n  				{\r\n  					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\3", $item);\r\n  				}\r\n  			<CMSPHP:CACHE>\r\n  			}\r\n  \r\n  			if (strstr($cms_mod[''value''][22], ''{last}'') || strstr($cms_mod[''value''][23], ''{last}''))\r\n  			{\r\n  			</CMSPHP:CACHE>\r\n  				if (($folder[''last_thing''] == ''page'') && ($folder[''last''] == $idcatside))\r\n  				{\r\n  					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\1", $item);\r\n  				}\r\n  				else\r\n  				{\r\n  					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\3", $item);\r\n  				}\r\n  			<CMSPHP:CACHE>\r\n  			}\r\n  			</CMSPHP:CACHE>\r\n  \r\n  			$temp .= $item;\r\n  		}\r\n  	}\r\n	}\r\n	\r\n	return $temp;\r\n}\r\n\r\n\r\nfunction print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_tree, $con_side, $mod;\r\n  \r\n  $cats_no_pages = explode(",", trim(str_replace('' '', '''', "MOD_VALUE[28]")));\r\n  \r\n	// Ordner ausgeben\r\n	if (is_array($folder[''children'']))\r\n	{\r\n		// Alle Unterordner dieses Ordners durchgehen\r\n		foreach ($folder[''children''] as $subfolder)\r\n		{\r\n			$idcat = $subfolder[''idcat''];\r\n			$active_side_is_start = <CMSPHP:CACHE>echo $con_side[$idcatside][''is_start''];</CMSPHP:CACHE>;\r\n      $active_cat = <CMSPHP:CACHE>echo $idcat;</CMSPHP:CACHE>;\r\n      $show_start_page = "<CMSPHP:CACHE>echo "MOD_VALUE[4]";</CMSPHP:CACHE>";\r\n				\r\n			<CMSPHP:CACHE>\r\n			// Falls "Ordner nur als aktiv markieren, wenn Startseite ange