'' == ''true'')\r\n			{\r\n                $item = str_replace(''{link}'', $con_side[ $GLOBALS[''SF_catinfos'']->getIdcatsideStartpage($idcat) ][''link''], $item);\r\n			} else {\r\n			     $item = str_replace(''{link}'', $con_tree[$idcat][''link''], $item);\r\n			} \r\n			\r\n			$item = str_replace(''{id}'', $idcat, $item);\r\n			$item = str_replace(''{depth}'', $depth, $item);\r\n			if (strstr($item, ''{tabindex}'') !== false)\r\n			{\r\n				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n			}\r\n\r\n			<CMSPHP:CACHE>\r\n			if (strstr($cms_mod[''value''][24], ''{first}'') || strstr($cms_mod[''value''][25], ''{first}''))\r\n			{\r\n			</CMSPHP:CACHE>\r\n				if ($folder[''first''])\r\n				{\r\n					$folder[''first''] = false;\r\n					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\1", $item);\r\n				}\r\n				else\r\n				{\r\n					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\3", $item);\r\n				}\r\n			<CMSPHP:CACHE>\r\n			}\r\n\r\n			if (strstr($cms_mod[''value''][24], ''{last}'') || strstr($cms_mod[''value''][25], ''{last}''))\r\n			{\r\n			</CMSPHP:CACHE>\r\n				if (($folder[''last_thing''] == ''folder'') && ($folder[''last''] == $idcat))\r\n				{\r\n					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\1", $item);\r\n				}\r\n				else\r\n				{\r\n					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\3", $item);\r\n				}\r\n			<CMSPHP:CACHE>\r\n			}\r\n			</CMSPHP:CACHE>\r\n\r\n			$subfolder_data = print_folder_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($subfolder, $depth+1);\r\n						\r\n			$temp .= str_replace("{folder}", $subfolder_data, $item);\r\n		}\r\n	}\r\n\r\n	return $temp;	\r\n}\r\n\r\n\r\nfunction build_navi_tree_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($start, $depth, $first = false)\r\n{\r\n	global $catlist, $con_tree, $con_side, $cms_side, $db, $mod, $cms_db, $lang;\r\n\r\n	$entry[''idcat''] = $start;\r\n	\r\n	if ($first)\r\n	{\r\n		$entry[''first''] = true;\r\n	}\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][3] != ''true'')\r\n	{\r\n		echo ''else if (!in_array($start, $mod[\\''path\\''])) return $entry;'';\r\n	}\r\n\r\n	if ($cms_mod[''value''][5] > 0)\r\n	{\r\n		echo ''if ($depth >= '' . (int)$cms_mod[''value''][5] . '') return $entry;'';\r\n	}\r\n	</CMSPHP:CACHE>\r\n	\r\n\r\n	// $hide == true <=> Ausgeschlossene Seiten und Ordner verbergen\r\n	$hide = <CMSPHP:CACHE>echo ($cms_mod[''value''][6] == ''true'') ? ''true'' : ''(!isset($cms_side[\\''view\\'']))'';</CMSPHP:CACHE>;\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][2] != ''folders'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		// Alle zu diesem Ordner gehoerigen Seiten zusammensuchen\r\n		foreach ($con_side as $idcatside=>$page)\r\n		{\r\n			// Seite ins Array aufnehmen, wenn ...\r\n			if ( ($page[''idcat''] == $start) &&\r\n                 (\r\n				   <CMSPHP:CACHE>\r\n				   // ... es keine Startseite ist (nur wenn keine Startseiten angezeigt werden sollen) und ...\r\n				   if ($cms_mod[''value''][''4''] == '''')\r\n				   {\r\n			         echo ''($con_side[$idcatside][\\''is_start\\''] == \\''0\\'') &&'';\r\n				   }\r\n				   </CMSPHP:CACHE>\r\n				   (\r\n				     // ... wir im Backend sind, oder ...\r\n				     isset($cms_side[''view'']) ||\r\n				     // ... diese online ist ...\r\n				     ($con_side[$idcatside][''online''] != 0)\r\n				   )\r\n				 ) &&\r\n				 // ... und diese Seite nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Seiten dort angezeigt werden sollen)\r\n				 (!$hide || !strstr('',<CMSPHP:CACHE>echo str_replace('' '', '''', $cms_mod[''value''][8]);</CMSPHP:CACHE>,'', ",$idcatside,"))\r\n			   )\r\n			{\r\n				$entry[''pages''][] = $idcatside;\r\n				\r\n				<CMSPHP:CACHE>\r\n				// "Notizen"-Felder der Seiten lesen, falls diese verwendet werden\r\n				if (strstr($cms_mod[''value''][22], ''{desc}'') || strstr($cms_mod[''value''][23], ''{desc}''))\r\n				{\r\n				</CMSPHP:CACHE>\r\n\r\n					$db->query("SELECT summary FROM `" . $cms_db[''side_lang''] . "` WHERE idlang=''" . $lang . "'' AND idside=''" . $idcatside . "''" );\r\n					if ($db->next_record())\r\n					{\r\n						$mod[''desc''][$idcatside] = $db->f(''summary'');\r\n					}\r\n\r\n				<CMSPHP:CACHE>\r\n				}\r\n				</CMSPHP:CACHE>\r\n			}\r\n		}\r\n		//print_r($entry[''pages'']);print_r($cms_mod);echo "xxxxxxxxxxxxxxxx";\r\n		// seiten nur wenn mindestens zwei \r\n		if (''MOD_VALUE[4]'' == ''gt1'') {\r\n		  if (count($entry[''pages'']) < 2) {\r\n		      unset($entry[''pages'']);\r\n		  }\r\n		}\r\n		// Seiten sortieren\r\n		if (is_array($entry[''pages'']))\r\n		{\r\n			foreach ($entry[''pages''] as $idcatside)\r\n			{\r\n				$sortarray[] = $con_side[$idcatside][''<CMSPHP:CACHE>echo $cms_mod[''value''][14];</CMSPHP:CACHE>''];\r\n			}\r\n			array_multisort($sortarray, <CMSPHP:CACHE>echo (($cms_mod[''value''][14] == ''name'') ? "SORT_STRING" : "SORT_NUMERIC") . ", " . $cms_mod[''value''][15];</CMSPHP:CACHE>, $entry[''pages'']);\r\n		}\r\n\r\n	<CMSPHP:CACHE>\r\n	}\r\n	</CMSPHP:CACHE>\r\n\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][2] != ''pages'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		\r\n		// Lokale Kopie von $catlist, weil der "foreach zeiger" fuer\r\n		// das globale Array durch die Rekursion verloren gehen wuerde\r\n		$catlist2 = $catlist;\r\n\r\n		// Fuer jeden Unterordner dieses Ordners diese Funktion aufrufen (Seiten und Unterordner zusammensuchen)\r\n		foreach ($catlist2 as $idcat)\r\n		{\r\n			// Unterordner ins Array aufnehmen, wenn ...\r\n			if ( ($con_tree[$idcat][''parent''] == $start) &&\r\n			     (\r\n			       // ... wir im Backend sind, oder ...\r\n			       isset($cms_side[''view'']) ||\r\n			       // ... dieser sichtbar/online ist ...\r\n			       ($con_tree[$idcat][''visible''] != 0)\r\n			     ) &&\r\n			     // ... und dieser Ordner nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Ordner dor