                  $mod[''searchstart'']=0;\r\n                                } else {\r\n                                        $mod[''repl_string''] .= "...";\r\n                                        $mod[''searchstart''] = strpos ($mod[''res''][''value'']," ",$mod[''searchstart'']);\r\n                                }\r\n\r\n                                if ($mod[''test''] == "true") {echo "Wort:''".$mod[''searchvalue''][0]."'' Start:".$mod[''searchstart'']."<hr />";}\r\n                                $mod[''repl_string''] .= substr($mod[''res''][''value''],$mod[''searchstart''],300)."...</p>";\r\n                        }\r\n                        $mod[''tpl_res''] = str_replace("{descr}",$mod[''repl_string''],$mod[''tpl_res'']);\r\n                        $mod[''tpl_res''] = str_replace("{nr}",$mod[''nr''],$mod[''tpl_res'']);\r\n                        $mod[''tpl_output''] .= $mod[''tpl_res''];\r\n                        $mod[''nr'']=$mod[''nr'']+1;\r\n                }\r\n        } else {\r\n                $mod[''tpl_res''] = "<p class=''MOD_VALUE[4]''>MOD_VALUE[19]</p>";\r\n                $mod[''tpl_output''] .= $mod[''tpl_res''];\r\n        }\r\n\r\n        $mod[''tpl_res_pre''] = str_replace("{count}",$result[''num_rows''],"MOD_VALUE[15]");\r\n\r\n        if($result[''num_rows''] > 0 && $mod[''res_limit''] != "" ) {\r\n                $mod[''anz''] = $result[''num_rows''];\r\n                $mod[''query''] = "&amp;w_70=$w_70&amp;w_71=$w_71&amp;w_72=$w_72&amp;w_73=$w_73&amp;w_74=$w_74&amp;what=$what&amp;how=$how&amp;modus=$modus&amp;sort=$sort&amp;suche=";\r\n                $mod[''query''] .= rawurlencode($suche);\r\n\r\n                // gesamtanzahl der seiten herausfinden\r\n                if("MOD_VALUE[11]" == "0"){\r\n                        $mod["anz"] = 1;\r\n                } else {\r\n                        $mod["anz"] = ceil($result[''num_rows''] / "MOD_VALUE[11]");\r\n                }\r\n\r\n                // wenn mod_value_32 leer ist, default-separator verwenden.\r\n                if ("MOD_VALUE[32]" == "")\r\n                {\r\n                        $separator = "&nbsp;&nbsp;|&nbsp;&nbsp;";\r\n                } else {\r\n                        $separator = "MOD_VALUE[32]";\r\n                }\r\n                $mod[''idcatlink'']= $con_side[$idcatside][''link''];\r\n                // vor/zurÃ¼ck-links anzeigen?\r\n                if ($mod["anz"]> 1)$mod["nav"] .= suchmodul_createPageLinks("MOD_VALUE[31]", $separator, "MOD_VALUE[30]", $page);\r\n      \r\n          if ($mod[''test''] == "true") {echo "<hr />Kontrolle Navigation: Anz ".$mod["anz"]." Seperator:".$separato.":   <br>Page: ".$page." [31]:MOD_VALUE[31] [30]:MOD_VALUE[30] [18]:MOD_VALUE[18]<br />";}\r\n\r\n                $mod[''tpl_nav''] = str_replace("{nav}", $mod[''nav''],"MOD_VALUE[18]");\r\n		\r\n		$mod[''tpl_res_pre'']  = str_replace("{nav}", $mod[''nav''],$mod[''tpl_res_pre'']);\r\n		$mod[''tpl_res_past''] = str_replace("{nav}", $mod[''nav''],"MOD_VALUE[17]");\r\n		\r\n		$mod[''tpl_output''] = $mod[''tpl_res_pre''].$mod[''tpl_output''].$mod[''tpl_res_past''];\r\n		\r\n	        echo $mod[''tpl_output''];\r\n		echo $mod[''tpl_nav''];\r\n		\r\n        } else {\r\n        	$mod[''tpl_output''] = $mod[''tpl_res_pre''].$mod[''tpl_output''];\r\n	        echo $mod[''tpl_output''];\r\n		\r\n	        echo "MOD_VALUE[17]";\r\n	}        	\r\n}\r\necho "MOD_VALUE[41]";\r\nunset($mod,$separator,$result);\r\n</CMSPHP>', '', 0, 1, 1, 1089569940, 1125877173, 'mod:931d2fc5c322bbcd2be1842c6a3c5db2:f7d22b0f', '', '', '', 0, '0', '', '1');
