es Bilds, HÃ¶he des Bilds Y, Breite des Watermarks, HÃ¶he des Bildes, Transparenz)\r\n			imagecopymerge($cms_image->imageHandle, $cms_watermark->imageHandle, $x, $y, 0, 0, $watermark_width, $watermark_height, $transition);\r\n			$cms_image->save($new_image);\r\n			$cms_image->free();\r\n			unset($cms_image);\r\n		}\r\n	}\r\n\r\n\r\n//Funktion zum auslesen der ThumbgrÃ¶Ãen\r\n	if (!function_exists(''get_thumb_size'')) {\r\n		function get_thumb_size($xy, $thumb) {\r\n			global $cfg_cms, $cfg_client;\r\n			require_once ''Image/Transform.php'';\r\n			$cms_image = Image_Transform::factory($cfg_cms[''image_mode'']);\r\n			$cms_image->load($thumb);\r\n\r\n			//if ($x != $cms_image->img_x || $y != $cms_image->img_y) $cms_image->resize($x, $y);\r\n			//$watermark = imagecreatefromstring(base64_decode($watermarkfile));\r\n                         $width = imagesx($cms_image->imageHandle);\r\n                         $height = imagesy($cms_image->imageHandle);\r\n			 $cms_image->free();\r\n			 unset($cms_image);\r\n\r\n			 if ($xy == ''x'') {\r\n			 return $width;\r\n			 }\r\n			 elseif ($xy == ''y'') {\r\n			 return $height;\r\n			 }\r\n\r\n		}\r\n	}\r\n\r\n\r\n\r\n//Funktion zum umwandeln von Bilder IDs in Pfade\r\n    if(! function_exists(get_path)){\r\n			    function get_path($id)\r\n			    {\r\n			     global $cfg_cms, $cfg_client, $cms_db, $db, $cms_mod;\r\n\r\n			     $sql = "SELECT A.*, B.dirname\r\n			     FROM\r\n			     ".$cms_db[''upl''] ." as A, ". $cms_db[''directory''] ." as B\r\n			     WHERE\r\n			     idupl=''".$id."''\r\n			     AND\r\n			     A.iddirectory = B.iddirectory";\r\n\r\n			     $db->query($sql);\r\n			     $db->next_record();\r\n			     $filepath = $cfg_client["upl_path"].$db->f(''dirname'').$db->f(''filename'');\r\n			     return $filepath;\r\n			    }\r\n			 }\r\n\r\n\r\n\r\n\r\n\r\n###########################################################################################################################\r\n#Hier ein paar Variablen, die wir Ã¼berall brauchen\r\n###########################################################################################################################\r\n\r\n//Variable fÃ¼r den Link zur aktuellen Seite mit Session ID\r\n$mod[''idcatlink''] =  $con_side[$idcatside][''link''];\r\n\r\n//Auslesen ob im Frontend-Editiert werden darf\r\n$mod[''frontedit''] = "MOD_VALUE[51]";\r\n\r\n//Auslesen ob mit Detailansicht gestartet wird\r\n$mod[''detailid_start''] = "MOD_VALUE[60]";\r\n\r\n//Auslesen ob Thumbs in der Detailansicht verwendet werden\r\n$mod[''detailid_thumb''] = "MOD_VALUE[61]";\r\n\r\n//Ordner beim Editieren im Frontend (inkl. Container fÃ¼r mehrere Galerien pro Seite\r\n$mod[''change_dir_string''] = "change_dir_".$cms_mod[''container''][''id''];\r\n$mod[''change_dir''] = ${$mod[''change_dir_string'']};\r\n\r\n//Anzahl der Bilder pro Seite\r\n$mod["number_of_pics_per_page"] = MOD_VALUE[6];\r\n\r\n//Anzahl der Bilder pro Zeile\r\n$mod["break_after_number_of_pics"] = MOD_VALUE[7];\r\n\r\n//Thumbtext im Projekt\r\n$mod["filename_suffix"] = $cfg_client["thumbext"];\r\n\r\n//Auslesen welche Galerieseite angezeigt werden soll\r\nif (isset($galerie))\r\n{\r\n     $mod["galerie"] = array();\r\n     $mod["galerie"] = explode("_",$galerie);\r\n     for ($i = 0; $i < count($mod["galerie"])/2; $i++)\r\n     { if ($cms_mod[''container''][''id''] == $mod["galerie"][$i*2])\r\n       { $id = $mod["galerie"][$i*2+1];\r\n       }\r\n       else\r\n       { $mod[''query''] .= "_" . $mod["galerie"][$i*2] . "_" . $mod["galerie"][$i*2+1];\r\n       }\r\n     }\r\n}\r\n\r\n//soll eine bestimmte Galerieseite angezeigt werden?\r\nif (!isset($id))\r\n{ if ("MOD_VALUE[5]" == "1")\r\n  { $id = 1;\r\n  }\r\n  else\r\n  { $id = "MOD_VALUE[5]";\r\n  }\r\n}\r\n\r\n//Falls Thumbs in der Detailansicht gezeigt werden umblÃ¤ttern bei Detailbildern\r\nif ($mod[''detailid_thumb''] AND !$galerie AND $detailid) {\r\n$id = ceil($detailid / $mod["number_of_pics_per_page"]);\r\n}\r\n\r\n// Automatisch die Dateilseiten zeigen und dann beim umblÃ¤ttern die neueste Detailid zeigen\r\nif(!isset($detailid) AND $sess->name != ''cms'' AND $mod[''detailid_start'']  AND !$galerie) {\r\n  $detailid = 1;\r\n} elseif (!isset($detailid) AND $sess->name != ''cms'' AND $mod[''detailid_start'']  AND $galerie) {\r\n $detailid = (($id-1) * $mod["number_of_pics_per_page"])+1 ;\r\n}\r\n\r\n\r\n##########################################################################################################################\r\n  // FRONTEND BEARBEITUNG (wird in der Detailansicht ausgeschlossen! Habe das fÃ¼r nicht sinnvoll gehalten. LÃ¤Ãt sich aber leicht wieder aktivieren.)\r\n##########################################################################################################################\r\n\r\n//Frontend-editieren\r\nif ($mod[''frontedit''] == true)\r\n\r\n    {\r\n\r\n    //bjoerns funktion\r\n    if(! function_exists(get_val)){\r\n    function get_val($value_name)\r\n    {\r\n     global $idcatside, $cms_db, $db, $con_side, $cms_mod;\r\n\r\n     $sql = "SELECT\r\n        *\r\n       FROM\r\n        ".$cms_db[''content_external''] ."\r\n       WHERE\r\n        idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n        AND container=''".$cms_mod[''container''][''id'']."''\r\n        AND idtype=''$value_name''";\r\n     $db->query($sql);\r\n     $db->next_record();\r\n\r\n     return $db->f(''value'');\r\n    }\r\n    }\r\n\r\n    if(! function_exists(set_val)){\r\n    function set_val($value_name, $value)\r\n    {\r\n     global $idcatside, $cms_db, $db, $con_side, $cms_mod;\r\n     //Schauen, ob es den Wert schon gibt\r\n     $sql = "SELECT\r\n        *\r\n       FROM\r\n        ".$cms_db[''content_external''] ."\r\n       WHERE\r\n        idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n        AND container=''".$cms_mod[''container''][''id'']."''\r\n        AND idtype=''$value_name''";\r\n     $db->query($sql);\r\n\r\n     //Es gibt den Wert schon -> wert aktuallisieren\r\n     if ($db->next_record()){\r\n      $sql = "UPDATE\r\n         ". $cms_db[''content_external''] ."\r\n        SET\r\n         value=''$value''\r\n        WHERE\r\n         idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n         AND container=''".$cms_mod[''container''][''id'']."''\r\n         AND idtype=''$value_name''";\r\n     }\r\n     //Es gibt den Wert noch nicht, neu in tabelle einfÃ¼gen\r\n     else{\r\n      $sql = "INSERT INTO\r\n         ". $cms_db[''content_external''] ."\r\n         (idsidelang, container, idtype, value)\r\n        VALUES\r\n         (''".$con_side[$idcatside][''idsidelang'']."'',\r\n          ''".$cms_mod[''container''][''id'']."'',\r\n          ''$value_name'', ''$value'')";\r\n     }\r\n\r\n     $db->query($sql);\r\n\r\n    //Letzte Ãnderung Datum Ã¤ndern!\r\n    $sql = "UPDATE ". $cms_db[''side_lang'']. " SET lastmodified=''".time()."'' WHERE idsidelang=''".$con_side[$idcatside][''idsidelang'']."'' ";\r\n                    $db->query($sql);\r\n\r\n     //Cache lÃ¶schen\r\n     //Seite fÃ¼rs frontend neu generieren, da sich ein Wert geÃ¤ndert hat\r\n     change_code_status($idcatside, 1, ''idcatside'');\r\n\r\n    }\r\n    }\r\n\r\n    //Editierwechsel beachten\r\n                            if  ($mod[''change_dir'']) {\r\n                                     //Formular ist abgeschickt worden\r\n                                    //Neuen Wert in die DB einfÃ¼gen\r\n                                    set_val(''galerie_folder'', $mod[''change_dir'']);\r\n                                            }\r\n\r\n    //Wert aus der DB holen\r\n    $mod[''galerie_folder''] = get_val(''galerie_folder'');\r\n\r\n\r\n    //Bearbeitenbutton\r\n    $aktverz = $mod[''galerie_folder''];\r\n    if ($cms_side[''edit'']) {\r\n\r\n    //Startverzeichnis auslesen\r\n    $mod[''startdir''] = "MOD_VALUE[52]";\r\n\r\n    if ($mod[''startdir'']) {\r\n\r\n    //Startverzeichnis Namen auslesen\r\n    $sql = "SELECT A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.iddirectory=''".$mod[''startdir'']."''\r\n    ORDER BY dirname";\r\n    $db -> query($sql);\r\n    $db->next_record();\r\n\r\n    $mod[''startdirname''] =  $db->f(''dirname'');\r\n\r\n     }\r\n\r\n\r\n    if ($mod[''startdirname'']) {\r\n\r\n    //Dateimanager Verzeichnisse auslesen mit Startverzeichnis\r\n    $sql = "SELECT A.iddirectory, A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.idclient=''".$client."''\r\n        AND A.status & 0x05 = 0x00\r\n        AND A.dirname like ''".$mod[''startdirname'']."%''\r\n\r\n    ORDER BY dirname";\r\n    $result = mysql_query($sql);\r\n\r\n    }else{\r\n\r\n    //Dateimanager Verzeichnisse auslesen\r\n    $sql = "SELECT A.iddirectory, A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.idclient=''".$client."''\r\n        AND A.status & 0x05 = 0x00\r\n\r\n    ORDER BY dirname";\r\n    $result = mysql_query($sql);\r\n\r\n    }\r\n\r\n\r\n\r\n    //Aktion auf aktuelle Seite legen\r\n    $action = $con_side[$idcatside][''link''];\r\n    //Bearbeitungsformular ausgeben\r\n    echo "<div align=\\"center\\"><form method=\\"POST\\" action=\\"$action\\"><select name=\\"change_dir_".$cms_mod[''container''][''id'']."\\" size=\\"1\\">";\r\n\r\n    while ($row = mysql_fetch_array($result, MYSQL_ASSOC))\r\n    {\r\n\r\n    if($perm->have_perm(0x01, "folder", $row[''iddirectory'']))\r\n    {\r\n    if ($mod[''galerie_folder''] == $row[''iddirectory''])\r\n    { echo "<option  selected value=\\""; }\r\n    else { echo "<option value=\\"";}\r\n      echo $row[''iddirectory''];\r\n      echo "\\">";\r\n      echo $row[''dirname''];\r\n      echo "</option>";\r\n    }}\r\n    echo"</select>";\r\n    echo"<input type=\\"submit\\" value=\\"Submit\\">";\r\n\r\n		//KÃ¤stchen fÃ¼r Watermark neu Berechnung einfÃ¼gen wenn Watermarkpfad vorhanden\r\n		if (($mod[''watermark_thumb_id'']) OR ($mod[''watermark_thumb_id''])) {\r\n		    echo"<input type=\\"checkbox\\" name=\\"resample_watermarks\\"> Wasserzeichen neu berechnen?";\r\n		   }\r\n\r\necho"</form></div>";\r\n\r\n\r\n    unset($cms_side[''edit'']);\r\n    }\r\n\r\n\r\n    }\r\n    //END-Frontend-Editieren\r\n    else\r\n    {\r\n    //Wenn der Ordner in der Konfiguration eingestellt wird\r\n    $mod[''galerie_folder''] = "MOD_VALUE[4]";\r\n}\r\n\r\n\r\n##########################################################################################################################\r\n  // Daten ermitteln\r\n##########################################################################################################################\r\n//Ist das Verzeichnis in der Datenbank? False bedeutet ja! Weiter unten in der if schleife wird das nochmal mÃ¶glichweise nochmal Ã¼berschrieben!\r\n//Das ist wichtig fÃ¼r das Bearbeiten im Frontend. When false wird die Galerie angezeigt, wenn true bedeutet das, dass der Wert in der DB noch nicht\r\n//gesetzt ist, was zu einem SQL-Fehler fÃ¼hren wÃ¼rde, der so abgefangen wird!\r\n$mod[''no_dir''] = false;\r\n\r\n\r\n//diese if-schleife fÃ¤ngt den SQL-Fehler ab wenn im FE noch kein Verzeicnis festgelegt wurde\r\nif ( ($mod[''galerie_folder''] ==false) AND ($mod[''frontedit''] == true))\r\n	{\r\n	$mod[''error_no_dir''] = "<div align=\\"center\\" class=\\"{font1}\\">Diese Galerie ist noch nicht eingerichtet worden.</div>";\r\n	$mod[''error_no_dir''] = str_replace("{font1}", "MOD_VALUE[1]", $mod[''error_no_dir'']);\r\n	echo $mod[''error_no_dir''];\r\n	$mod[''no_dir''] = true;\r\n	}\r\nelse\r\n	{\r\n    //Mit dieser if-Schleife wird fÃ¼r Unterordner gesorgt und fÃ¼r "Alle Ordner"\r\n    if ( ($mod[''galerie_folder''] ==false) AND ($mod[''frontedit''] == false))\r\n        {\r\n        $sql = "SELECT A.*, B.dirname\r\n                            FROM " . $cms_db[''upl''] . " as A, ". $cms_db[''directory''] ." as B\r\n\r\n                            WHERE\r\n                