'routings''][''idcat:''.$idcat]);\r\n		if ($ln_routing[''idcat_temp''] > 0) {\r\n		  $idcat = $ln_routing[''idcat_temp''];\r\n		  $idcatside = $SF_catinfos->getIdcatsideStartpage($idcat);\r\n		}\r\n	}\r\n}\r\n\r\n\r\n</CMSPHP:CACHE>\r\n\r\n\r\n\r\n//Description of cats\r\nif(! function_exists(get_cat_desc)){\r\n    function get_cat_desc($a)\r\n{\r\nglobal $db,$cms_db;\r\n$sql  = "SELECT\r\n			*\r\n		FROM \r\n	   ".$cms_db[''cat_lang'']." \r\n		WHERE idcatlang = ''".$a."''";\r\n$db->query($sql);\r\n$db->next_record();\r\nreturn $db->f(''description''); \r\n}}\r\n\r\n//Descritpion of pages\r\nif(! function_exists(get_side_desc)){\r\n    function get_side_desc($a,$which)\r\n{\r\nglobal $db,$cms_db;\r\n$sql  = "SELECT\r\n			*\r\n		FROM \r\n	   ".$cms_db[''side_lang'']." LEFT JOIN\r\n		".$cms_db[''cat_side'']." USING(IDSIDE)\r\n		WHERE \r\nidcatside = ''".$a."''";\r\n$db->query($sql);\r\n$db->next_record();\r\n\r\nif ($which == ''desc'') {\r\nreturn $db->f(''meta_description''); }\r\nelseif ($which == ''summary''){\r\nreturn $db->f(''summary''); }\r\n}}\r\n\r\n\r\n\r\n\r\n\r\n\r\n//global $evil_global_variable_for_easy_tabindex_implementation, $mod;\r\n$evil_global_variable_for_easy_tabindex_implementation = 1;\r\n\r\n$mod[''desc''] = array();\r\n\r\n// idcat des Start-Ordners besorgen\r\n<CMSPHP:CACHE>\r\nif(! function_exists(''nav_negotiate_startlevel'') ){\r\nfunction nav_negotiate_startlevel( $current_cat, $startlevel ){\r\n        global $con_tree;\r\n\r\n        //echo "$cat, $searcharray <br />";//return false;\r\n        if (empty($current_cat) || $current_cat == 0 || ! is_int($current_cat) || ! is_int($startlevel)  ) {\r\n                return $current_cat;\r\n        }\r\n\r\n        $levels = array();\r\n        array_push($levels, $current_cat);\r\n        $cat = $current_cat;\r\n        while(true) {\r\n                $cat = (int) $con_tree[$cat][''parent''];\r\n                array_push($levels, $cat);\r\n                if ($cat == 0) break;\r\n        }\r\n        $levels_rev = array_reverse($levels);\r\n        //print_r($levels_rev);exit;\r\n\r\n        if ($levels_rev[$startlevel] > 0) {\r\n                return $levels_rev[$startlevel];\r\n        } else {\r\n                return $current_cat;\r\n        }\r\n}}\r\n\r\n\r\nif ($cms_mod[''value''][1] == ''root'')\r\n{\r\n        $cms_mod[''value''][1] = ''0'';\r\n}\r\nelseif ($cms_mod[''value''][1] == ''this'')\r\n{\r\n        $cms_mod[''value''][1] = $idcat;\r\n}\r\n\r\n$mod[''startlevel'']  = (int) "MOD_VALUE[29]";\r\nif ($mod[''startlevel''] > 0) {\r\n  $cms_mod[''value''][1] = (string) nav_negotiate_startlevel( (int) $idcat, $mod[''startlevel'']);\r\n}\r\n\r\nif (!$cms_mod[''value''][14])\r\n{\r\n	// Update von einer Version kleiner als 0.9-dev => Diese Variablen wurden noch nicht durch die Modulkonfiguration gesetzt\r\n	$cms_mod[''value''][14] = ''sortindex'';\r\n	$cms_mod[''value''][15] = ''SORT_ASC'';\r\n}\r\n\r\n\r\n// Anzahl darzustellender Ebenen auf 0 setzen, falls unsinnige Werte angegeben wurden\r\nif (!is_numeric($cms_mod[''value''][5]) || ($cms_mod[''value''][5] < 0))\r\n{\r\n	$cms_mod[''value''][5] = 0;\r\n}\r\n\r\n// Startnummer fuer tabindex auf 1 setzen, falls unsinnige Werte angegeben wurden\r\nif (!is_numeric($cms_mod[''value''][13]) || ($cms_mod[''value''][13] < 1))\r\n{\r\n	$cms_mod[''value''][13] = 1;\r\n}\r\n</CMSPHP:CACHE>\r\n\r\n\r\n/*\r\n * Hilfs-Funktionen\r\n */\r\n\r\nfunction print_folder_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][16] == ''true'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		$output = print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n		$output .= print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n	<CMSPHP:CACHE>\r\n	}\r\n	else\r\n	{\r\n	</CMSPHP:CACHE>\r\n		$output = print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n		$output .= print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth);\r\n	<CMSPHP:CACHE>\r\n	}\r\n	</CMSPHP:CACHE>\r\n	\r\n	if (strlen($output) > 0)\r\n	{\r\n		$tpl = str_replace(''{id}'', $folder[''idcat''], "MOD_VALUE[26]"); \r\n		$output = str_replace(''{layer}'', $output, $tpl);\r\n	}\r\n	\r\n	return $output;\r\n}\r\n\r\n\r\nfunction print_folder_helper_pages_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_side, $mod, $sess;\r\n	\r\n	$cats_no_pages = explode(",", trim(str_replace('' '', '''', "MOD_VALUE[28]")));\r\n	\r\n	// Seiten ausgeben\r\n	if (isset($folder[''pages'']))\r\n	{  \r\n	  $pagevals = array_values($folder[''pages'']);\r\n	  if(! in_array($con_side[ $pagevals[''0''] ][''idcat''],$cats_no_pages) || (''MOD_VALUE[6]'' != ''true'' && $sess->name == ''sefrengo'' ) )\r\n    {\r\n	   \r\n  		foreach ($folder[''pages''] as $idcatside)\r\n  		{\r\n  			$template = ($idcatside == <CMSPHP:CACHE>echo $idcatside;</CMSPHP:CACHE>) ? "MOD_VALUE[23]" : "MOD_VALUE[22]";\r\n  			$item = str_replace(''{name}'', htmlspecialchars($con_side[$idcatside][''name''], ENT_COMPAT, ''UTF-8''), $template);\r\n  			$item = str_replace(''{desc}'', htmlspecialchars(get_side_desc($idcatside,''desc''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{summary}'', htmlspecialchars(get_side_desc($idcatside,''summary''), ENT_COMPAT, ''UTF-8''), $item);\r\n  			$item = str_replace(''{link}'', $con_side[$idcatside][''link''], $item);\r\n  			$item = str_replace(''{id}'', $idcatside, $item);\r\n  			$item = str_replace(''{depth}'', $depth, $item);\r\n  			if (strstr($item, ''{tabindex}'') !== false)\r\n  			{\r\n  				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n  			}\r\n  \r\n  			<CMSPHP:CACHE>\r\n  			if (strstr($cms_mod[''value''][22], ''{first}'') || strstr($cms_mod[''value''][23], ''{first}''))\r\n  			{\r\n  			</CMSPHP:CACHE>\r\n  				if ($folder[''first''])\r\n  				{\r\n  					$folder[''first''] = false;\r\n  					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\1", $item);\r\n  				}\r\n  				else\r\n  				{\r\n  					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\3", $item);\r\n  				}\r\n  			<CMSPHP:CACHE>\r\n  			}\r\n  \r\n  			if (strstr($cms_mod[''value''][22], ''{last}'') || strstr($cms_mod[''value''][23], ''{last}''))\r\n  			{\r\n  			</CMSPHP:CACHE>\r\n  				if (($folder[''last_thing''] == ''page'') && ($folder[''last''] == $idcatside))\r\n  				{\r\n  					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\1", $item);\r\n  				}\r\n  				else\r\n  				{\r\n  					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\3", $item);\r\n  				}\r\n  			<CMSPHP:CACHE>\r\n  			}\r\n  			</CMSPHP:CACHE>\r\n  \r\n  			$temp .= $item;\r\n  		}\r\n  	}\r\n	}\r\n	\r\n	return $temp;\r\n}\r\n\r\n\r\nfunction print_folder_helper_folders_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($folder, $depth)\r\n{\r\n	global $evil_global_variable_for_easy_tabindex_implementation, $con_tree, $con_side, $mod;\r\n  \r\n  $cats_no_pages = explode(",", trim(str_replace('' '', '''', "MOD_VALUE[28]")));\r\n  \r\n	// Ordner ausgeben\r\n	if (is_array($folder[''children'']))\r\n	{\r\n		// Alle Unterordner dieses Ordners durchgehen\r\n		foreach ($folder[''children''] as $subfolder)\r\n		{\r\n			$idcat = $subfolder[''idcat''];\r\n			$active_side_is_start = <CMSPHP:CACHE>echo $con_side[$idcatside][''is_start''];</CMSPHP:CACHE>;\r\n      $active_cat = <CMSPHP:CACHE>echo $idcat;</CMSPHP:CACHE>;\r\n      $show_start_page = "<CMSPHP:CACHE>echo "MOD_VALUE[4]";</CMSPHP:CACHE>";\r\n				\r\n			<CMSPHP:CACHE>\r\n			// Falls "Ordner nur als aktiv markieren, wenn Startseite angezeigt wird" gewaehlt wurde\r\n			// und die aktuelle (die angezeigte) Seite keine Startseite ist, ...\r\n			if (($cms_mod[''value''][9] == ''true'') && ($con_side[$idcatside][''is_start''] != ''1''))\r\n			{\r\n			</CMSPHP:CACHE>\r\n				// ... die Vorlage (Template) "Ordner" nehmen, ...\r\n				$template = "MOD_VALUE[24]";\r\n			<CMSPHP:CACHE>\r\n			}\r\n			else\r\n			{\r\n			</CMSPHP:CACHE>\r\n				// ... andernfalls die Vorlage "Aktiver Ordner" benutzen, wenn der Unterordner\r\n				// der aktuelle ("angezeigte") Ordner ist und sonst die Vorlage "Ordner" verwenden.\r\n				$template = ($idcat == <CMSPHP:CACHE>echo $idcat;</CMSPHP:CACHE>) ? "MOD_VALUE[25]" : "MOD_VALUE[24]";\r\n				<CMSPHP:CACHE>\r\n				if ($cms_mod[''value''][11] == ''true'')\r\n				{\r\n				</CMSPHP:CACHE>\r\n					if (in_array($idcat, $mod[''active'']))\r\n					{\r\n					   //aktive (25) oder verfolgte (27) kategorie\r\n					   if ($idcat == $active_cat){\r\n					        //ist aktiver ordner\r\n					        //print_r($folder);\r\n                  if ( in_array($idcat, $cats_no_pages)  \r\n                  || ($active_side_is_start == ''1'' && ($show_start_page == ''false'' || ($show_start_page == ''gt1'' && count($subfolder[''pages'']) < 2) ) ) \r\n                 /*|| count($folder[''pages'']) < 2 && */ ) {\r\n                    $template = "MOD_VALUE[25]";\r\n                  //ist verfolter ordner\r\n                  } else {\r\n                    $template = "MOD_VALUE[27]";\r\n                  }\r\n              } else {\r\n                  $template = "MOD_VALUE[27]";\r\n              }\r\n					}\r\n				<CMSPHP:CACHE>\r\n				}\r\n			}\r\n			</CMSPHP:CACHE>\r\n\r\n			$item = str_replace(''{name}'', htmlspecialchars($con_tree[$idcat][''name''], ENT_COMPAT, ''UTF-8''), $template);\r\n			$item = str_replace(''{desc}'', htmlspecialchars(get_cat_desc($idcat), ENT_COMPAT, ''UTF-8''), $item);\r\n			//$item = str_replace(''{link}'', $con_tree[$idcat][''link''], $item);\r\n			if (''MOD_VALUE[17]'' == ''true'')\r\n			{\r\n                $item = str_replace(''{link}'', $con_side[ $GLOBALS[''SF_catinfos'']->getIdcatsideStartpage($idcat) ][''link''], $item);\r\n			} else {\r\n			     $item = str_replace(''{link}'', $con_tree[$idcat][''link''], $item);\r\n			} \r\n			\r\n			$item = str_replace(''{id}'', $idcat, $item);\r\n			$item = str_replace(''{depth}'', $depth, $item);\r\n			if (strstr($item, ''{tabindex}'') !== false)\r\n			{\r\n				$item = str_replace(''{tabindex}'', $evil_global_variable_for_easy_tabindex_implementation++, $item);\r\n			}\r\n\r\n			<CMSPHP:CACHE>\r\n			if (strstr($cms_mod[''value''][24], ''{first}'') || strstr($cms_mod[''value''][25], ''{first}''))\r\n			{\r\n			</CMSPHP:CACHE>\r\n				if ($folder[''first''])\r\n				{\r\n					$folder[''first''] = false;\r\n					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\1", $item);\r\n				}\r\n				else\r\n				{\r\n					$item = preg_replace("/{first}(.*?)({else}(.*?))?{\\/first}/", "\\\\3", $item);\r\n				}\r\n			<CMSPHP:CACHE>\r\n			}\r\n\r\n			if (strstr($cms_mod[''value''][24], ''{last}'') || strstr($cms_mod[''value''][25], ''{last}''))\r\n			{\r\n			</CMSPHP:CACHE>\r\n				if (($folder[''last_thing''] == ''folder'') && ($folder[''last''] == $idcat))\r\n				{\r\n					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\1", $item);\r\n				}\r\n				else\r\n				{\r\n					$item = preg_replace("/{last}(.*?)({else}(.*?))?{\\/last}/", "\\\\3", $item);\r\n				}\r\n			<CMSPHP:CACHE>\r\n			}\r\n			</CMSPHP:CACHE>\r\n\r\n			$subfolder_data = print_folder_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($subfolder, $depth+1);\r\n						\r\n			$temp .= str_replace("{folder}", $subfolder_data, $item);\r\n		}\r\n	}\r\n\r\n	return $temp;	\r\n}\r\n\r\n\r\nfunction build_navi_tree_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($start, $depth, $first = false)\r\n{\r\n	global $catlist, $con_tree, $con_side, $cms_side, $db, $mod, $cms_db, $lang;\r\n\r\n	$entry[''idcat''] = $start;\r\n	\r\n	if ($first)\r\n	{\r\n		$entry[''first''] = true;\r\n	}\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][3] != ''true'')\r\n	{\r\n		echo ''else if (!in_array($start, $mod[\\''path\\''])) return $entry;'';\r\n	}\r\n\r\n	if ($cms_mod[''value''][5] > 0)\r\n	{\r\n		echo ''if ($depth >= '' . (int)$cms_mod[''value''][5] . '') return $entry;'';\r\n	}\r\n	</CMSPHP:CACHE>\r\n	\r\n\r\n	// $hide == true <=> Ausgeschlossene Seiten und Ordner verbergen\r\n	$hide = <CMSPHP:CACHE>echo ($cms_mod[''value''][6] == ''true'') ? ''true'' : ''(!isset($cms_side[\\''view\\'']))'';</CMSPHP:CACHE>;\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][2] != ''folders'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		// Alle zu diesem Ordner gehoerigen Seiten zusammensuchen\r\n		foreach ($con_side as $idcatside=>$page)\r\n		{\r\n			// Seite ins Array aufnehmen, wenn ...\r\n			if ( ($page[''idcat''] == $start) &&\r\n                 (\r\n				   <CMSPHP:CACHE>\r\n				   // ... es keine Startseite ist (nur wenn keine Startseiten angezeigt werden sollen) und ...\r\n				   if ($cms_mod[''value''][''4''] == '''')\r\n				   {\r\n			         echo ''($con_side[$idcatside][\\''is_start\\''] == \\''0\\'') &&'';\r\n				   }\r\n				   </CMSPHP:CACHE>\r\n				   (\r\n				     // ... wir im Backend sind, oder ...\r\n				     isset($cms_side[''view'']) ||\r\n				     // ... diese online ist ...\r\n				     ($con_side[$idcatside][''online''] != 0)\r\n				   )\r\n				 ) &&\r\n				 // ... und diese Seite nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Seiten dort angezeigt werden sollen)\r\n				 (!$hide || !strstr('',<CMSPHP:CACHE>echo str_replace('' '', '''', $cms_mod[''value''][8]);</CMSPHP:CACHE>,'', ",$idcatside,"))\r\n			   )\r\n			{\r\n				$entry[''pages''][] = $idcatside;\r\n				\r\n				<CMSPHP:CACHE>\r\n				// "Notizen"-Felder der Seiten lesen, falls diese verwendet werden\r\n				if (strstr($cms_mod[''value''][22], ''{desc}'') || strstr($cms_mod[''value''][23], ''{desc}''))\r\n				{\r\n				</CMSPHP:CACHE>\r\n\r\n					$db->query("SELECT summary FROM `" . $cms_db[''side_lang''] . "` WHERE idlang=''" . $lang . "'' AND idside=''" . $idcatside . "''" );\r\n					if ($db->next_record())\r\n					{\r\n						$mod[''desc''][$idcatside] = $db->f(''summary'');\r\n					}\r\n\r\n				<CMSPHP:CACHE>\r\n				}\r\n				</CMSPHP:CACHE>\r\n			}\r\n		}\r\n		//print_r($entry[''pages'']);print_r($cms_mod);echo "xxxxxxxxxxxxxxxx";\r\n		// seiten nur wenn mindestens zwei \r\n		if (''MOD_VALUE[4]'' == ''gt1'') {\r\n		  if (count($entry[''pages'']) < 2) {\r\n		      unset($entry[''pages'']);\r\n		  }\r\n		}\r\n		// Seiten sortieren\r\n		if (is_array($entry[''pages'']))\r\n		{\r\n			foreach ($entry[''pages''] as $idcatside)\r\n			{\r\n				$sortarray[] = $con_side[$idcatside][''<CMSPHP:CACHE>echo $cms_mod[''value''][14];</CMSPHP:CACHE>''];\r\n			}\r\n			array_multisort($sortarray, <CMSPHP:CACHE>echo (($cms_mod[''value''][14] == ''name'') ? "SORT_STRING" : "SORT_NUMERIC") . ", " . $cms_mod[''value''][15];</CMSPHP:CACHE>, $entry[''pages'']);\r\n		}\r\n\r\n	<CMSPHP:CACHE>\r\n	}\r\n	</CMSPHP:CACHE>\r\n\r\n\r\n	<CMSPHP:CACHE>\r\n	if ($cms_mod[''value''][2] != ''pages'')\r\n	{\r\n	</CMSPHP:CACHE>\r\n		\r\n		// Lokale Kopie von $catlist, weil der "foreach zeiger" fuer\r\n		// das globale Array durch die Rekursion verloren gehen wuerde\r\n		$catlist2 = $catlist;\r\n\r\n		// Fuer jeden Unterordner dieses Ordners diese Funktion aufrufen (Seiten und Unterordner zusammensuchen)\r\n		foreach ($catlist2 as $idcat)\r\n		{\r\n			// Unterordner ins Array aufnehmen, wenn ...\r\n			if ( ($con_tree[$idcat][''parent''] == $start) &&\r\n			     (\r\n			       // ... wir im Backend sind, oder ...\r\n			       isset($cms_side[''view'']) ||\r\n			       // ... dieser sichtbar/online ist ...\r\n			       ($con_tree[$idcat][''visible''] != 0)\r\n			     ) &&\r\n			     // ... und dieser Ordner nicht ausgeschlossen wurde (oder wir im Backend sind und ausgeschlossene Ordner dort angezeigt werden sollen)\r\n			     (!$hide || !strstr('',<CMSPHP:CACHE>echo str_replace('' '', '''', $cms_mod[''value''][7]);</CMSPHP:CACHE>,'', ",$idcat,"))\r\n			   )\r\n			{\r\n				$entry[''children''][$idcat] = build_navi_tree_<CMSPHP:CACHE>echo $cms_mod[''container''][''id''];</CMSPHP:CACHE>($idcat, $depth+1);\r\n			}\r\n		}\r\n\r\n\r\n		// Ordner sortieren\r\n		if (is_array($entry[''children'']))\r\n		{\r\n			unset($sortarray);\r\n			<CMSPHP:CACHE>\r\n			if ($cms_mod[''value''][14] == ''sortindex'')\r\n			{\r\n			</CMSPHP:CACHE>\r\n				$catlist2 = array();\r\n				foreach ($catlist as $key=>$value)\r\n				{\r\n					if (is_array($entry[''children''][$value]))\r\n					{\r\n						$catlist2[$value] = $key;\r\n					}\r\n				}\r\n	\r\n				foreach ($entry[''children''] as $idcat => $v)\r\n				{\r\n					$sortarray[] = $catlist2[$idcat];\r\n				}\r\n				\r\n				array_multisort($sortarray, SORT_NUMERIC, <CMSPHP:CACHE>echo $cms_mod[''value''][15];</CMSPHP:CACHE>, $entry[''children'']);\r\n			<CMSPHP:CACHE>\r\n			}\r\n			else\r\n			{\r\n			</CMSPHP:CACHE>\r\n				foreach ($entry[''children''] as $idcat => $v)\r\n				{\r\n					$sortarray[] = $con_tree[$idcat][''<CMSPHP:CACHE>echo $cms_mod[''value''][14];</CMSPHP:CACHE>''];\r\n				}\r\n				array_multisort($sortarray, <CMSPHP:CACHE>echo (($cms_mod[''value''][14] == ''name'') ? "SORT_STRING" : "SORT_NUMERIC") . ", " . $cms_mod[''value''][15];</CMSPHP:CACHE>, $entry[''children'']);\r\n			<CMSPHP:CACHE>\r\n			}\r\n			</CMSPHP:CACHE>\r\n		}\r\n\r\n	<CMSPHP:CACHE>\r\n	}\r\n	</CMSPHP:CACHE>\r\n\r\n	if ($first)\r\n	{\r\n		<CMSPHP:CACHE>\r\n		if (strstr($cms_mod[''value''][22], ''{last}'') || strstr($cms_mod[''value''][23], ''{last}'') ||\r\n		    strstr($cms_mod[''value''][24], ''{last}'') || strstr($cms_mod[''value''][25], ''{last}''))\r\n		{\r\n			if ($cms_mod[''value''][16] == true)\r\n			{\r\n			</CMSPHP:CACHE>\r\n				$current = &$entry;\r\n				$parent = &$entry;\r\n				$last = false;\r\n				do\r\n				{\r\n					if (is_array($c